import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useLocation, useParams } from 'react-router-dom'
import apiServices from '../services/apiServices';
const Individualevent = () => {
  const [data,setData] = useState();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const id= queryParams.get("id");
  const formatDate = (dateString) => {
        const options = { year: 'numeric', month: 'short', day: '2-digit' };
        const date = new Date(dateString);
        return date.toLocaleDateString('en-US', options);
      };
      const fetchEvent = async (id) => {
        try {
          const response = await apiServices.fetchEvent(id);
          // const response = await axios.get(`https://swayam-website-d9b3d-default-rtdb.asia-southeast1.firebasedatabase.app/blogs/${id}.json`);
          console.log(response.data)
          setData(response.data)
        } catch (error) {
            console.error('Error saving blog:', error);
        }
      }
  useEffect(() => {
    fetchEvent(id);
  }, [])

  return (
    <>
    {data && <div className='getInvolvedPage' style={{width:"60%"}}>
        <h3 style={{margin:"5px auto"}}>{data.eventtitle}</h3>
        <h4>
        <a href={data.eventurl} style={{margin:"0px auto"}}>{data.eventurl}</a>
        </h4>
        <p>{data.eventdate}</p>
        <p>{data.eventDescription}</p>
        <div style={{display:"flex"}}>
        <img src={data.eventimageone} style={{marginLeft:"10px"}} width={'50%'} alt="Description of the image"></img>
        <img src={data.eventimagetwo} style={{marginLeft:"10px"}} width={'50%'} alt="Description of the image"></img>
        </div>
        <h3>
        </h3>
    </div>}
    </>
  )
}

export default Individualevent
